import React, { Component } from 'react';
import Modal from 'react-modal';
import './list-service.css';

const modalStyle = {
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1000,
  },
  content: {
    width: '80%',
    maxWidth: '80vh',
    margin: 'auto',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  },
};

class ListServicos extends Component {
  constructor() {
    super();
    this.state = {
      showModal: false,
      showForm: false, 
      selectedProduct: null,
    };
  }

  openModal = (product) => {
    this.setState({ showModal: true, selectedProduct: product });
  };

  closeModal = () => {
    this.setState({ showModal: false, selectedProduct: null });
  };

  openForm = () => {
    this.setState({ showForm: true });
  };

  closeForm = () => {
    this.setState({ showForm: false });
  };

  render() {
    const { selectedProduct, showForm } = this.state;
    const { products } = this.props;

    return (
      <div className='list-services'>
        {products.map((product, index) => (
          <div className='product' key={index}>
            <img src={product.image}  />
            <h3 className='product-title'>{product.category}</h3>
            <h4 className='product-title'>{product.name}</h4>
            <h2 className='value'>{product.price}</h2>
            <span>{product.description}</span>
            <a className='contrate' href='#' onClick={this.openForm}>
              Contrate Já
            </a>
            <a className='saiba' href='#' onClick={() => this.openModal(product)}>
              Saiba mais
            </a>
          </div>
        ))}

        <Modal isOpen={this.state.showModal} onRequestClose={this.closeModal} style={modalStyle}>
          {selectedProduct && (
            <div className='modal'>
              <button className='close' onClick={this.closeModal}>
                X
              </button>
              <div className='modal-title'>
              <img src={selectedProduct.image}  />
              <h3 className='product-title'>{selectedProduct.name}</h3>
              </div>

              <span>{selectedProduct.modalDescription}</span>
            </div>
          )}
        </Modal>

        <Modal isOpen={showForm} onRequestClose={this.closeForm} style={modalStyle}>
          {showForm && (
            <div className='modal'>
              <button className='close' onClick={this.closeForm}>
                X
              </button>
              <h3>Formulário de Contratação</h3>
              <form>
              <div className='form-field'>
                <label htmlFor='name'>Nome</label>
                <input type='text' id='name' name='name' required />
              </div>

              <div className='form-field'>
                <label htmlFor='company'>Empresa</label>
                <input type='text' id='company' name='company' />
              </div>

              <div className='form-field'>
                <label htmlFor='email'>Endereço de e-mail</label>
                <input type='email' id='email' name='email' required />
              </div>

              <div className='form-field'>
                <label htmlFor='phone'>Número de telefone</label>
                <input type='tel' id='phone' name='phone' />
              </div>

              <div className='form-field'>
                <label htmlFor='message'>Mensagem</label>
                <textarea id='message' name='message' rows='4'></textarea>
              </div>

              <div className='form-field'>
                <label htmlFor='services'>Serviço de Interesse</label>
                <select id='services' name='services'>
                  <option value='Migration'>Migração para a Nuvem</option>
                  <option value='CloudOps'>Operações na Nuvem</option>
                  <option value='SaaSFactory'>SaaS Factory</option>
                </select>
              </div>

              <div className='form-field'>
                <label htmlFor='how_heard'>Como você ouviu falar de nós?</label>
                <select id='how_heard' name='how_heard'>
                  <option value='Google Search'>Pesquisa no Google</option>
                  <option value='Social Media'>Mídias Sociais</option>
                  <option value='Recommendation'>Recomendação de Amigo</option>
                  <option value='Other'>Outro</option>
                </select>
              </div>

              <div className='form-field'>
                <input type='submit' value='Enviar' className='submit-button' />
              </div>
            </form>
            </div>
          )}
        </Modal>
      </div>

    );
  }
}

export default ListServicos;
