import React, { Component } from 'react';
import Modal from 'react-modal';
import { CSSTransition } from 'react-transition-group'; // Importe CSSTransition
import './list-service.css';

const modalStyle = {
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1000,
  },
  content: {
    width: '80%',
    maxWidth: '80vh',
    margin: 'auto',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  },
};

class ListServicos extends Component {
  constructor() {
    super();
    this.state = {
      showModal: false,
      selectedProduct: null,
    };
  }

  openModal = (product) => {
    this.setState({ showModal: true, selectedProduct: product });
  };

  closeModal = () => {
    this.setState({ showModal: false, selectedProduct: null });
  };

  render() {
    const { selectedProduct } = this.state;
    const { products } = this.props;

    return (
      <div className='list-services'>
        {products.map((product, index) => (
          <div className='product' key={index}>
            <h3 className='product-title'>{product.category}</h3>
            <h4 className='product-title'>{product.name}</h4>
            <h2 className='value'>{product.price}</h2>
            <span>{product.description}</span>
            <a className='contrate' href='#'>
              Contrate Já
            </a>
            <a className='saiba' href='#' onClick={() => this.openModal(product)}>
              Saiba mais
            </a>
          </div>
        ))}

<CSSTransition
  in={this.state.showModal}
  timeout={300}
  classNames="modal"
  unmountOnExit
>
  <Modal
    isOpen={this.state.showModal}
    onRequestClose={this.closeModal}
    style={modalStyle}
  >
    {selectedProduct && (
      <div>
        <button onClick={this.closeModal}>X</button>
        <h3 className='product-title'>{selectedProduct.category}</h3>
        <h4 className='product-name'>{selectedProduct.name}</h4>
        <h2 className='value'>{selectedProduct.price}</h2>
        <span>{selectedProduct.modalDescription}</span>
        <button className='contrate-button' onClick={this.closeModal}>Contrate Agora</button>

        {/* Estrutura dos tópicos */}
        <div className='migration-topics-modal'>
          <div className='topic'>
            {/* Imagem do Tópico 1 */}
            <img src='caminho/para/imagem1.png' alt='Imagem 1' />
            <p>Texto descritivo do Tópico 1</p>
          </div>
          <div className='topic'>
            {/* Imagem do Tópico 2 */}
            <img src='caminho/para/imagem2.png' alt='Imagem 2' />
            <p>Texto descritivo do Tópico 2</p>
          </div>
          <div className='topic'>
            {/* Imagem do Tópico 3 */}
            <img src='caminho/para/imagem3.png' alt='Imagem 3' />
            <p>Texto descritivo do Tópico 3</p>
          </div>
        </div>
      </div>
    )}
  </Modal>
</CSSTransition>
      </div>
    );
  }
}

export default ListServicos;
