import React, { useState } from 'react';
import './servicos.css';
import logo from '../../assets/logo1.png';
import ListServicos from '../../components/servicos/listServices.js';

export default function Servicos() {
  // Exemplo de produtos fictícios
  const seusProdutos = [
    {
      category: 'MIGRAÇÃO',
      name: 'Valcann Cloud Migration',
      price: 'R$ 200,00',
      description: 'Planejamento, execução e otimização de estratégias de migração para a Nuvem. Construção de jornada de adoção de Nuvem, plano de execução e migração.',
      modalDescription : '<div class="tyJCtd mGzaTb Depvyb baZpAe"><div id="h.qmtysgc13j9u" class="CobnVe yMxPgf  aP9Z7e"></div><h2 id="h.qmtysgc13j9u_l" dir="ltr" class="zfr3Q JYVBee CDt4Ke " style=""><span class="C9DxTc " style="font-family: Lato, Arial; font-weight: 700;">Planejamento, execução e otimização de estratégias de migração para a Nuvem, com construção de jornada de adoção de Nuvem, plano de execução e migração.</span></h2><br><div id="h.mk6sq5mwqwqe" class="GV3q8e yMxPgf  aP9Z7e"></div><h3 id="h.mk6sq5mwqwqe_l" dir="ltr" class="zfr3Q OmQG5e CDt4Ke " style=""><span class="C9DxTc " style="">Planejar</span></h3><p dir="ltr" class="zfr3Q CDt4Ke " style=""><span class="C9DxTc " style="">Dimensionamento (rightsizing) e planejamento de capacidade, design de arquitetura da solução (AS IS e TO BE) e construção do roadmap de implantação, considerando todas as restrições de negócios e de tecnologia envolvidas no projeto.</span></p><div id="h.eyfg0znumqvu" class="GV3q8e yMxPgf  aP9Z7e"></div><h3 id="h.eyfg0znumqvu_l" dir="ltr" class="zfr3Q OmQG5e CDt4Ke " style=""><span class="C9DxTc " style="">Construir</span></h3><p dir="ltr" class="zfr3Q CDt4Ke " style=""><span class="C9DxTc " style="">Construção do projeto arquitetural proposto. Implementação da infraestrutura, considerando o planejamento especificado no projeto. Alinhamento da construção às práticas de Well-Archictected (Excelência Operacional, Segurança, Resiliência, Performance e Otimização de Custos).</span></p><div id="h.oyjri4uf1k6u" class="GV3q8e yMxPgf  aP9Z7e"></div><h3 id="h.oyjri4uf1k6u_l" dir="ltr" class="zfr3Q OmQG5e CDt4Ke " style=""><span class="C9DxTc " style="">Executar</span></h3><p dir="ltr" class="zfr3Q CDt4Ke " style=""><span class="C9DxTc " style="">Execução e otimização das cargas de trabalhos implementadas na nuvem. Entrada em produção com operação assistida, 24 x 7, com suporte especializado e dedicado.</span></p></div>'
    },
    {
      category: 'OPERAÇÃO',
      name: 'Valcann Cloud Operations',
      price: 'R$ 300,00',
      description: 'Com o Valcann CloudOps, entregamos serviços de gerenciamento contínuo de infraestrutura na Nuvem. Cloud NOC, Segurança, Automação e Orquestração, Monitoramento.',
    },
    {
      category: 'MODERNIZAÇÃO',
      name: 'Valcann SaaS Factory',
      price: 'R$ 400,00',
      description: 'Soluções especializadas de modernização de aplicações para Software como Serviço (SaaS) e DevOps. Modernização de aplicações como serviço.',
    },
  ];

  return (
    <div className="content servicos">
      <div className="title">
        <img src={logo} alt="Logo" />
        <span>Nossos Serviços</span>
      </div>
      <div className="subtitle">
        <h2>Conheça os nossos serviços</h2>
        <span>
          Com os serviços da Valcann, você recebe suporte total em toda a sua jornada na Nuvem, livrando-se da preocupação e da complexidade de fazer isso por conta própria. Não importa qual seu estágio de adoção e desafio, nossas soluções podem ajudar a sua empresa a extrair o máximo da Nuvem.
        </span>
      </div>

      <ListServicos products={seusProdutos} />
    </div>
  );
}
