import React, { Component, useState } from 'react';
import Modal from 'react-modal';
import './list-service.css';
import InputMask from 'react-input-mask';

const modalStyle = {
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1000,
  },
  content: {
    width: '80%',
    maxWidth: '80vh',
    minHeight: 'fit-content',
    maxHeight: '90%',
    margin: 'auto',
    padding: '3vh',
    borderRadius: '8px',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  },
};

class ListServicos extends Component {
  constructor() {
    super();
    this.state = {
      showModal: false,
      showForm: false,
      selectedProduct: null,
      instancesQtd: 1,
      storage: 1,
      backup: 1,
      productPrice: 0,
    };
  }

updatePrice = () => {
  const { selectedProduct, instancesQtd, storage, backup } = this.state;

  const instancesPrice = instancesQtd * selectedProduct.price;
  const storagePrice = storage * selectedProduct.price * 0.3;
  const backupPrice = backup * selectedProduct.price * 0.1;

  const totalPrice =
    selectedProduct.price + instancesPrice + storagePrice + backupPrice;

  this.setState({ productPrice: totalPrice });
};

  handleFormChange = (event) => {
    const { name, value } = event.target;
    this.setState(
      {
        [name]: parseFloat(value),
      },
      () => {
        this.updatePrice();
      }
    );
  };

  handleContratar = () => {
    const { nome, email, empresa } = this.state;
    if (!nome || !email || !empresa) {
      alert('Preencha todos os campos obrigatórios.');
      return;
    } else {
      // implementação do backend
      alert('contratado');
    }
  };

  openModal = (product) => {
    this.setState({ showModal: true, selectedProduct: product });
  };

  closeModal = () => {
    this.setState({ showModal: false, selectedProduct: null });
  };

  openForm = (product) => {
    this.setState({ showForm: true, selectedProduct: product });
  };

  closeForm = () => {
    this.setState({ showForm: false, selectedProduct: null });
  };

  render() {
    const { selectedProduct, showForm, productPrice } = this.state;
    const { products } = this.props;

    return (
      <div className='list-services'>
        {products.map((product, index) => (
          <div className='product' key={index}>
            <img src={product.image} alt={product.name} />
            <h3 className='product-title'>{product.category}</h3>
            <h4 className='product-title'>{product.name}</h4>
            <span>{product.description}</span>
            <a className='contrate' href='#' onClick={() => this.openForm(product)}>
              Contrate Já
            </a>
            <a className='saiba' href='#' onClick={() => this.openModal(product)}>
              Saiba mais
            </a>
          </div>
        ))}

        <Modal isOpen={this.state.showModal} onRequestClose={this.closeModal} style={modalStyle}>
          {selectedProduct && (
            <div className='modal'>
              <button className='close' onClick={this.closeModal}>
                X
              </button>
              <div className='modal-title'>
                <img src={selectedProduct.image} alt={selectedProduct.name} />
                <h3 className='product-title'>{selectedProduct.name}</h3>
              </div>

              <span>{selectedProduct.modalDescription}</span>
            </div>
          )}
        </Modal>

        <Modal isOpen={showForm} onRequestClose={this.closeForm} style={modalStyle}>
          {showForm && (
            <div className='modal'>
              <button className='close' onClick={this.closeForm}>
                X
              </button>
              <h3>Formulário de Contratação</h3>
              <form>
                <div className='selected'>
                  <div className='servicoEscolhido'>
                    <label htmlFor='servicoEscolhido'>Serviço Escolhido :</label>
                    <input
                      type='text'
                      id='servicoEscolhido'
                      name='servicoEscolhido'
                      value={selectedProduct.category + ': ' + selectedProduct.name}
                      disabled
                    />
                  </div>
                  <div className='value'>
                    <label htmlFor='valor'>Valor do Serviço :</label>
                    <input
                      type='text'
                      id='valor'
                      name='valor'
                      value={`R$ ${this.state.productPrice.toFixed(2)}`}
                      disabled
                    />
                  </div>
                </div>

                <label htmlFor='name'>Nome Completo :</label>
                <input
                  type='text'
                  id='name'
                  name='name'
                  required
                />

                <label htmlFor='email'>Email :</label>
                <input
                  type='email'
                  id='email'
                  name='email'
                  required
                />

                <label htmlFor='contact'>Contato :</label>
                <InputMask
                  mask='(99)99999-9999'
                  id='contact'
                  name='contact'
                  maskChar='_'
                  required
                />

                <label htmlFor='name'>Nome da sua empresa :</label>
                <input
                  type='text'
                  id='empresa'
                  name='empresa'
                  required
                />

                {selectedProduct.formprice}

                <label htmlFor='observacoes'>Observações:</label>
                <textarea
                  id='observacoes'
                  name='observacoes'
                  rows='10'
                ></textarea>

                <button onClick={this.handleContratar}> Contratar </button>
              </form>
            </div>
          )}
        </Modal>
      </div>
    );
  }
}

export default ListServicos;
