import React, { Component } from 'react';
import Modal from 'react-modal';
import { CSSTransition } from 'react-transition-group'; // Importe CSSTransition
import './list-service.css';

const modalStyle = {
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1000,
  },
  content: {
    width: '80%',
    maxWidth: '80vh',
    margin: 'auto',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  },
};

class ListServicos extends Component {

  render() {
    const { selectedProduct } = this.state;
    const { products } = this.props;

    return (
      <div className='list-services'>
        {products.map((product, index) => (
          <div className='product' key={index}>
            <a className='saiba' href='#' onClick={() => this.openModal(product)}>
              Saiba mais
            </a>
          </div>
        ))}

        <CSSTransition
          in={this.state.showModal}
          timeout={300} 
          classNames={{
            enter: 'modal-entering',
            enterActive: 'modal-entered',
            exit: 'modal-exiting',
            exitActive: 'modal-exited',
          }}
          unmountOnExit
        >
          <Modal
            isOpen={this.state.showModal}
            onRequestClose={this.closeModal}
            style={modalStyle}
          >
            {selectedProduct && (
              <div className='modal'>
                <button className='close' onClick={this.closeModal}>
                  X
                </button>
                <h3 className='product-title'>{selectedProduct.name}</h3>
                <span>{selectedProduct.modalDescription}</span>
              </div>
            )}
          </Modal>
        </CSSTransition>
      </div>
    );
  }
}

export default ListServicos;
