import React, { Component } from 'react';
import Modal from 'react-modal';
import './list-service.css';

// Estilo personalizado para o modal
const modalStyle = {
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1000,
  },
  content: {
    width: '80%',
    maxWidth: '500px',
    margin: 'auto',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.2)',
  },
};

class ListServicos extends Component {
  constructor() {
    super();
    this.state = {
      showModal: false,
      selectedProduct: null,
    };
  }

  // Função para abrir o modal
  openModal = (product) => {
    this.setState({ showModal: true, selectedProduct: product });
  };

  // Função para fechar o modal
  closeModal = () => {
    this.setState({ showModal: false, selectedProduct: null });
  };

  render() {
    const { selectedProduct } = this.state;

    return (
      <div className='list-services'>
        {this.props.products.map((product, index) => (
          <div className='product' key={index}>
            <h3 className='product-title'>{product.category}</h3>
            <h4 className='product-title'>{product.name}</h4>
            <h2 className='value'>{product.price}</h2>
            <span>{product.description}</span>
            <button onClick={() => this.openModal(product)}>Contrate já</button>
          </div>
        ))}

        <Modal
          isOpen={this.state.showModal}
          onRequestClose={this.closeModal}
          style={modalStyle}
        >
          {selectedProduct && (
            <div>
              <h3 className='product-title'>{selectedProduct.category}</h3>
              <h4 className='product-title'>{selectedProduct.name}</h4>
              <h2 className='value'>{selectedProduct.price}</h2>
              <span>{selectedProduct.description}</span>
              <button onClick={this.closeModal}>Fechar</button>
            </div>
          )}
        </Modal>
      </div>
    );
  }
}

export default ListServicos;
