import React, { Component } from 'react';
import './header.css';
import headerLogo from '../../assets/footer-logo.png';
import scrolledLogo from '../../assets/footer-logo.png'; // Importe a imagem da logo rolada

class Header extends Component {
  constructor() {
    super();
    this.state = {
      scrolled: false,
    };
  }

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

  handleScroll = () => {
    if (window.scrollY > 50) {
      this.setState({ scrolled: true });
    } else {
      this.setState({ scrolled: false });
    }
  };

  render() {
    const { scrolled } = this.state;
    const logoSrc = scrolled ? scrolledLogo : headerLogo; // Alterne a imagem da logo com base no estado rolado
    const headerClass = scrolled ? 'header scrolled' : 'header';

    return (
      <div className={headerClass}>
        <div className='header-content'>
          <div className='logo'>
            <img src={logoSrc} alt='Logo' />
          </div>
          <div className='menu'>
            <ul>
              <li><a href='#'>In√≠cio</a></li>
              <li><a href='#'>Contrate</a></li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}

export default Header;
